property.value:
if (any(ls() == 'Method') == FALSE) {Method <- 'gaussian'}

Factors <- c('Factor1', 'Factor2', 'Factor3', 'Factor4', 'Factor5')
Names   <- c('fact1', 'fact2', 'fact3', 'fact4', 'fact5')
FU      <- c('f1', 'f2', 'f3', 'f4', 'f5')
mark <- 1
facts <- c()
fs <- c()
row_vec <- c()
for (f in Factors) {
	if (any(ls() == f) == TRUE) {
		assign(Names[mark], get(f))
		assign(FU[mark], unique(get(f)))
		row_vec <- c(row_vec, length(unique(get(f))))
		facts <- c(facts, Names[mark])
		fs <- c(fs, FU[mark])
		mark <- mark + 1
	}
}

var <- as.numeric(Variable)
var[is.na(var) == TRUE] <- 1


mod <- c(paste('var', facts[1], sep = ' ~ '))
vec <- data.frame(get(facts[1]))
if (length(facts) > 1) {
	for (i in 2:length(facts)) {
		mod <- paste(mod, facts[i], sep = ' + ')
		vec <- data.frame(vec, get(facts[i]))
	}
}
vec <- data.frame(vec, var)
colnames(vec) <- c(facts, 'var')

model <- glm(formula(mod), data = vec, family = Method)
summary(model)
