property.value:
if (is.character(Dependent)) {
	Dependent <- factor(Dependent)
}

model <- lm(Dependent ~ Independent)

plot(Independent, Dependent, xlab="Independent", ylab="Dependent")

if (is.numeric(Dependent)) {
	abline(model)
	intercept <- as.numeric(model$coefficient[1])
	slope <- as.numeric(model$coefficient[2])
	intercept
	slope
} 
if (is.character(Dependent)) {
	results <- anova(model)
	results
}
